import{_ as i,c as l,o as a,b as t}from"./app.05abc48f.js";const g=JSON.parse('{"title":"常用数学公式","description":"","frontmatter":{},"headers":[],"relativePath":"doc/ai/common_math_formulas.md","lastUpdated":1696925297000}'),x={name:"doc/ai/common_math_formulas.md"},n=t('<h1 id="常用数学公式" tabindex="-1">常用数学公式 <a class="header-anchor" href="#常用数学公式" aria-hidden="true">#</a></h1><h2 id="常用函数导数" tabindex="-1">常用函数导数 <a class="header-anchor" href="#常用函数导数" aria-hidden="true">#</a></h2><ol><li><p><strong>多项式函数</strong>:</p><ul><li>( (ax^n)&#39; = nax^{(n-1)} )</li></ul></li><li><p><strong>指数函数和对数函数</strong>:</p><ul><li>( (e^x)&#39; = e^x )</li><li>( (a^x)&#39; = \\ln(a) \\cdot a^x )</li><li>( (\\ln(x))&#39; = \\frac{1}{x} )</li></ul></li><li><p><strong>三角函数</strong>:</p><ul><li>( (\\sin(x))&#39; = \\cos(x) )</li><li>( (\\cos(x))&#39; = -\\sin(x) )</li><li>( (\\tan(x))&#39; = \\sec^2(x) )</li></ul></li><li><p><strong>反三角函数</strong>:</p><ul><li>( (\\arcsin(x))&#39; = \\frac{1}{\\sqrt{1-x^2}} )</li><li>( (\\arccos(x))&#39; = -\\frac{1}{\\sqrt{1-x^2}} )</li><li>( (\\arctan(x))&#39; = \\frac{1}{1+x^2} )</li></ul></li><li><p><strong>双曲函数</strong>:</p><ul><li>( (\\sinh(x))&#39; = \\cosh(x) )</li><li>( (\\cosh(x))&#39; = \\sinh(x) )</li><li>( (\\tanh(x))&#39; = \\text{sech}^2(x) )</li></ul></li><li><p><strong>反双曲函数</strong>:</p><ul><li>( (\\text{arsinh}(x))&#39; = \\frac{1}{\\sqrt{x^2 + 1}} )</li><li>( (\\text{arcosh}(x))&#39; = \\frac{1}{\\sqrt{x^2 - 1}} )</li><li>( (\\text{artanh}(x))&#39; = \\frac{1}{1-x^2} )</li></ul></li><li><p><strong>对数函数</strong>:</p><ul><li>( (\\log_a(x))&#39; = \\frac{1}{x \\cdot \\ln(a)} )</li></ul></li><li><p><strong>幂函数</strong>:</p><ul><li>( (x^a)&#39; = ax^{(a-1)} )</li></ul></li></ol><p>注意，这些公式是在函数的定义域内有效，且有时需要其他条件（比如，函数必须是可微的）。</p><h2 id="函数的和、差、积、商的求导法则" tabindex="-1">函数的和、差、积、商的求导法则 <a class="header-anchor" href="#函数的和、差、积、商的求导法则" aria-hidden="true">#</a></h2><ol><li><p><strong>和/差的求导法则</strong>:</p><ul><li>如果 ( f ) 和 ( g ) 是可导函数，则它们的和/差的导数为： [ (f + g)&#39; = f&#39; + g&#39; ] [ (f - g)&#39; = f&#39; - g&#39; ]</li></ul></li><li><p><strong>积的求导法则（乘法法则）</strong>:</p><ul><li>如果 ( f ) 和 ( g ) 是可导函数，则它们的积的导数为： [ (fg)&#39; = f&#39;g + fg&#39; ]</li></ul></li><li><p><strong>商的求导法则</strong>:</p><ul><li>如果 ( f ) 和 ( g ) 是可导函数且 ( g \\neq 0 )，则它们的商的导数为： [ \\left(\\frac{f}{g}\\right)&#39; = \\frac{f&#39;g - fg&#39;}{g^2} ]</li></ul></li></ol><h2 id="基本积分表" tabindex="-1">基本积分表 <a class="header-anchor" href="#基本积分表" aria-hidden="true">#</a></h2><ol><li>$ \\int kdx = kx + C $</li><li>$ \\int x^{\\alpha} dx = \\frac{x^{\\alpha+1}}{\\alpha+1} + C \\quad (\\alpha \\neq 1) $</li><li>$ \\int \\frac{dx}{x} = \\ln |x| + C $</li><li>$ \\int \\frac{dx}{1 + x^2} = \\arctan x + C $</li><li>$ \\int \\frac{dx}{\\sqrt{1 - x^2}} = \\arcsin x + C $</li><li>$ \\int \\cos dx = \\sin x + C $</li><li>$ \\int \\sin dx = -\\cos x + C $</li><li>$ \\int \\frac{dx}{\\cos^2 x} = \\int \\sec^2 x dx = \\tan x + C $</li><li>$ \\int \\frac{dx}{\\sin^2 x} = \\int \\csc^2 x dx = -\\cot x + C $</li><li>$ \\int \\sec x \\tan x dx = \\sec x + C $</li><li>$ \\int \\csc x \\cot x dx = -\\csc x + C $</li><li>$ \\int e^x dx = e^x + C $</li><li>$ \\int a^x dx = \\frac{a^x}{\\ln a} + C $</li><li>$ \\int \\sinh x dx = \\cosh x + C $</li><li>$ \\int \\cosh x dx = \\sinh x + C $</li><li>$ \\int \\tan x dx = -\\ln |\\cos x| + C $</li><li>$ \\int \\cot x dx = \\ln |\\sin x| + C $</li><li>$ \\int \\sec x dx = \\ln |\\sec x + \\tan x| + C $</li><li>$ \\int \\csc x dx = -\\ln |\\csc x + \\cot x| + C $</li><li>$ \\int \\frac{dx}{a^2 + x^2} = \\frac{1}{a} \\arctan \\frac{x}{a} + C $</li><li>$ \\int \\frac{dx}{a^2 - x^2} = \\frac{1}{2a} \\ln \\frac{a + x}{a - x} + C $</li><li>$ \\int \\frac{dx}{\\sqrt{a^2 - x^2}} = \\arcsin \\frac{x}{a} + C $</li><li>$ \\int \\frac{dx}{\\sqrt{x^2 + a^2}} = \\ln \\left( x + \\sqrt{x^2 + a^2} \\right) + C $</li><li>$ \\int \\frac{dx}{\\sqrt{x^2 - a^2}} = \\ln \\left( x + \\sqrt{x^2 - a^2} \\right) + C $</li></ol>',8),r=[n];function s(c,o,e,d,f,h){return a(),l("div",null,r)}const p=i(x,[["render",s]]);export{g as __pageData,p as default};
